slides.css:
.slide-content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  text-align: center;
}

h1 {
  font-size: 3.5em;
  margin-bottom: 0.3em;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.2);
}

.subtitle {
  font-size: 1.5em;
  opacity: 0.9;
}

.feature-slide {
  padding: 60px;
  background: #f8f9fa;
}

h2 {
  text-align: center;
  color: #6366f1;
  font-size: 2.5em;
  margin-bottom: 60px;
}

.features {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
}

.feature {
  text-align: center;
  padding: 30px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}

.emoji {
  font-size: 3em;
  display: block;
  margin-bottom: 20px;
}

.feature h3 {
  color: #333;
  margin-bottom: 10px;
}

.feature p {
  color: #666;
}

.creative-slide {
  background: linear-gradient(45deg, #ff6b6b, #feca57, #48dbfb, #ff9ff3);
  background-size: 400% 400%;
  animation: gradient 15s ease infinite;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px;
}

@keyframes gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.content-box {
  background: rgba(255, 255, 255, 0.95);
  padding: 60px;
  border-radius: 20px;
  box-shadow: 0 20px 60px rgba(0,0,0,0.3);
  max-width: 700px;
}

h2 {
  color: #333;
  margin-bottom: 30px;
  font-size: 2.5em;
}

.feature-list {
  list-style: none;
  padding: 0;
}

.feature-list li {
  font-size: 1.3em;
  padding: 12px 0;
  color: #555;
  border-bottom: 1px solid #eee;
}

.feature-list li:last-child {
  border-bottom: none;
}

.cta-slide {
  background: #0f172a;
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 60px;
  text-align: center;
}

h1 {
  font-size: 3.5em;
  margin-bottom: 60px;
  background: linear-gradient(to right, #60a5fa, #a78bfa);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.steps {
  display: flex;
  flex-direction: column;
  gap: 20px;
  max-width: 600px;
}

.step {
  background: rgba(255, 255, 255, 0.1);
  padding: 20px 30px;
  border-radius: 12px;
  font-size: 1.3em;
  border-left: 4px solid #60a5fa;
  transition: all 0.3s;
}

.step:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateX(10px);
}
